import React, { Component } from 'react'
// import PropTypes from 'prop-types'
import styled from 'styled-components'

export class product extends Component {
  static propTypes = {

  }

  render() {
    return (
      <Container>
        <Wrapper>
          <ProductImg>
            <Image_ src = "https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087" 
            alt=""/>
          </ProductImg> 
          <ProductInfo>

            <Brand>Apollo</Brand>

            <Name>Running Short</Name>

            <AttributesContainer>
              <AttributeType>SIZE</AttributeType>
              <AttributeItems>
                Color  
              </AttributeItems>                  
              <AttributeType>COLOR</AttributeType> 
              <AttributeItems>4,6,9,2</AttributeItems>   
              <Price>
                <AttributeType>PRICE</AttributeType>
                <AttributeType>$50.00</AttributeType>
              </Price>
            </AttributesContainer> 

            <Button>ADD TO CART</Button>

            <ProductDescription>
              <br/><p>A good gaming console. Plays games of PS4! Enjoy if you can buy it mwahahahaha</p>
            </ProductDescription>

          </ProductInfo>
        </Wrapper>
      </Container>
    )
  }
}

export default product

const Container = styled.div`

`
const Wrapper = styled.div`
  display: flex;
  justify-content: center;

`
const ProductImg = styled.div`
  margin-right: 25px;
  // margin-top: -10px;
   `
const Image_ = styled.img`
  max-height: 90%;
  margin-left: -20px;
  border: 0.5px ;

   `
const ProductInfo = styled.div`
  // margin-left: 30px;
  margin-top: 120px;
  display: flex;
  flex-direction: column;
  // justify-content: center;
`
const Brand = styled.span`
  font-weight: 400;
  font-size: 30px;
  line-height: 27px;
  margin-top: 15px;
  margin-bottom: 15px;
`
const Name = styled.span`
  line-height: 27px;
  width: 192;
  display: flex;
  align-items: center;
  font-weight: 250;
  font-size: 30px;
  font-style: normal;
  color: #1D1F22;
`
const AttributesContainer = styled.div`
  margin-top:20px;
`
const AttributeType = styled.span`
  font-weight: 700;
  margin-bottom: 10px;

`
const AttributeItems = styled.div`
  margin-top: 10px;
  margin-bottom: 10px;
`
const Price = styled.span`
  display: flex;
  flex-direction: column;

`
const Button = styled.div`
  font-weight: 600;
  color: white;
  background-color: #5ECE7B;
  height: 40px;
  width: 256px;
  display: flex;
  justify-content: center;
  border-radius: 3px; 
  opacity: 0.85;
  font-size: 13px;
  cursor: pointer;
  margin-top: 20px;
  align-items: center;  

`
const ProductDescription = styled.div`
  width: 256px;
  display: flex;
  flex-wrap: wrap;

`
