import React, { Component } from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'
import { CategoryItem } from './CategoryItem';
import { Query } from 'react-apollo'
import { gql } from 'graphql-tag'
import CategoryName from './CategoryName'


const CATEGORIES_QUERY = gql`
    query categoriesQuery{ 
        categories{
          name
          products{
            name
            brand
            id
            gallery
            description
            inStock
            category
          }
        }
    }
`

class Categories extends Component {
  static propTypes = {

  }
  state={
    clothes_data:[]
  }

  render() {
    return (
      <Container>
        {/* <Button onClick={()=>filterClothes('clothes')}>Clothes</Button> */}
        <Query query={CATEGORIES_QUERY}>
          {
            ({ data, loading, error }) => {
              if (loading) return <h3></h3>
              if (error) console.log(error.message)
              console.log(data)

              {

                const filterClothes = (cat) => {
                  const results = data.categories.filter(currData => {
                    return currData.name === cat}
                    )
                  this.setState({clothes_data: results})
                  console.log('okay')
                  }
                filterClothes('clothes')
                e.preventDefault()  
              }
              // const { name } = data.category
              return <Wrap>
                      {/* <CategoryName name={name}/> */}                    
                      {
                        data.categories.map(catItem => (
                          <CategoryItem key={catItem.name} catItem={catItem}/>
                        ))
                      }

                    </Wrap>
            }
          }
        </Query>
      </Container>
    )
  }
}

export default Categories

const Container = styled.div`
  // display: flex;
`
const Wrap = styled.div`
   display: flex;
   flex-direction: column;
`
const Button = styled.div`
  font-weight: 600;
  color: white;
  background-color: #5ECE7B;
  height: 40px;
  width: 250px;
  display: flex;
  justify-content: center;
  border-radius: 3px; 
  opacity: 0.85;
  font-size: 13px;
  cursor: pointer;
  margin-top: 20px;
  align-items: center;  
  `
