import React, { Component } from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'
import logo from '../images/logo.png'
import down_arrow from '../images/down_arrow.png'
import dollar from '../images/dollar.png'
import basket_ from '../images/basket_.png'
import vector from '../images/vector.jpg'

export class Navbar extends Component {
  static propTypes = {

  }

  render() {

    return (
      <Container>
          <Wrapper>
            <NavLeft>
                <Left_first>
                    <LeftItem>WOMEN</LeftItem>
                </Left_first>
                <Left_second>
                    <LeftItem>MEN</LeftItem>
                </Left_second>
                <Left_third>
                    <LeftItem>KIDS</LeftItem>
                </Left_third>
            </NavLeft>
            <NavCenter>
               <LogoHolder src={vector} alt=""/> 
               <NavLogo src={logo} alt="" />
            </NavCenter>
            <NavRight>
                <CurrencyItems>
                    <Currency src={dollar} alt=""/>                             
                    <DownArrow src={down_arrow} alt=""/> 
                    <Bag src={basket_} alt="" />
                </CurrencyItems>

            </NavRight>
          </Wrapper>
      </Container>
    )
  }
}

export default Navbar

const Container = styled.div`
    background-color: ;
    height: 60px;

  `
const Wrapper = styled.div`
    display: flex;
    justify-content: space-between;
    padding: 10px 20px;

`
const NavLeft = styled.div`
    flex: 1;
    display: flex;
    margin: 10px;
    align-items: center;
    // justify-content: center
    margin-left: 45px;  
`
const NavCenter = styled.div`
    flex: 1;
    margin-left: 30px;
    display: flex;
    justify-content: center;
    align-items: center;

`
const NavRight = styled.div`
    flex: 1;
    display: flex;
    justify-content: flex-end;
`
const Left_first = styled.span`
    margin-right: 25px;
`
const Left_second = styled.span`
    margin-right: 25px;
`
const Left_third = styled.span`
    margin-right: 25px;
`
const LeftItem = styled.span`
    font-weight: 100;
    cursor: pointer;
    `
const LogoHolder = styled.img`
    background: linear-gradient(316)
`
const NavLogo = styled.img`
    background-color: green;
`
const Currency = styled.img`
    margin-right: 5px;
`
const CurrencyItems = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 30px;

`
const DownArrow = styled.img`
    height: 8px;
    width: 14px;
    margin-top: 4px;
`
const Bag = styled.img`
    margin-right:30px;
    height: 18px;
    margin-left: 15px;

`
