class Categories extends Component {
  static propTypes = {

  }
  state={
    clothes_data:[]
  }

  // filterClothes = (cat) => {
  //   const results = this.data.categories.filter((catItem) => {
  //     return catItem.name === catItem.name
  //     console.log(catItem[0].name)
  //   })
  //   this.setState({clothes_data: results})

  // }

  render() {

    return (

      <Container>

        <Query query={CATEGORIES_QUERY} >
          {
            ({ data, loading, error }) => {
              if (loading) return <h3></h3>
              if (error) console.log(error.message)
              console.log(data.categories)

              const filterClothes = (cat) => {
                const results = data.categories.filter((catItem) => {
                  return catItem.name === catItem.name
                  console.log(catItem[0].name)
                })
                this.setState({clothes_data: results})

              }

              <Button onClick={()=>{filterClothes("clothes")}}>Clothes</Button>
              // const { name } = data.category
              return <Wrap>
                      {/* <CategoryName name={name}/> */}                    
                      {
                        data.categories.map(catItem => (
                          <CategoryItem key={catItem.name} catItem={catItem}/>
                        ))
                      }

                    </Wrap>
            }
          }
        </Query>
      </Container>
    )
  }
}

export default Categories

const Container = styled.div`
  // display: flex;
`
const Wrap = styled.div`
   display: flex;
   flex-direction: column;
`
