import { Query } from 'react-apollo'
import { gql } from 'graphql-tag'
import { Params } from 'react-router-dom'
import basket from '../images/basket.png'

const PRODUCT_QUERY = gql`
    query productQuery($title: String){ 
        category(input: {title: $title}){
          name
          products{
            inStock
            name
            id
            brand
            description
            gallery
          }
      }
    }
`

export class ProductItem extends Component {
    state = {

        prod: []
    }
    static propTypes = {

    }

    // filterClothes = (cat) => {
    //     const result = this.state.prod.filter((currentData) =>{
    //         return currentData.category === cat 
    //     });
    //     this.setState({prod: this.filterClothes(cat)})
    //     // console.log('name')
    // }

    render() {
        const { name, brand, gallery, id, description, prices, inStock, category } = this.props.prod;

        // const catego = this.props

        // const filterClothes = (cat) => {
        //     const result = catego.filter((currentData) => {
        //         return currentData.category===cat
        //     });

        //     this.setState({ prod: result(cat) })
        //     console.log(cat)
        // }

        return (
            <Container>
                {/* <Button onClick={() => filterClothes('tech')}> Tech </Button> */}
                <Wrapper>
                    <ProductImage>
                        <Image_ src={gallery[0]}/>
                    </ProductImage>
                    <ProductSelector>
                        <SelectPro src={basket}/>
                    </ProductSelector>
                    <Link to={`/product/${id}`}>
                        <ProductInfo>
                            <Brand>Apollo</Brand>
                            <Name>{name}</Name>
                            <PriceItems>
                                <CurrencySymbol>$</CurrencySymbol>
                                <Amount>80.00</Amount>
                            </PriceItems>
                        </ProductInfo>
                    </Link>
                </Wrapper>
            </Container>
        )
    }
}

export default ProductItem

const Container = styled.div`
    flex: 1;
