                    <ProductImage>
                        <Image_ src={gallery[0]} />
                    </ProductImage>
                    <Link to={`/product/${id}`}>
                        <ProductSelector>
                            <SelectIcon src={basket} />
                        </ProductSelector>
                    </Link>
                    <ProductInfo>
                        <Brand>Apollo</Brand>
                        <Name>{name}</Name>
                        <PriceItems>
                            {/* <CurrencySymbol>$</CurrencySymbol> */}
                            <Amount>{symbol} {amount}</Amount>
