import React, { Component } from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'
import { Link } from 'react-router-dom'
import { Query } from 'react-apollo'
import { gql } from 'graphql-tag'
// import GET_CATEGORIES from './Data'


const PRODUCT_QUERY = gql`
    query productQuery($title: String){ 
        category(input: {title: $title}){
          name
          products{
            inStock
            name
            id
            brand
            description
            gallery
          }
      }
    }
`

class Product extends Component {
    state = {
        Data: ({loading, error, data}),
        prod: []
    }
    static propTypes = {

    }

    // filterClothes = (cat) => {
    //     const result = this.state.prod.filter((currentData) =>{
    //         return currentData.category === cat 
    //     });
    //     this.setState({prod: this.filterClothes(cat)})
    //     // console.log('name')
    // }

    render() {
        const { name, brand, gallery, id, description, prices, inStock, category } = this.props.prod;
        const catego = this.props

        this.Data

        const filterClothes = (cat) => {
            const result = catego.filter((currentData) => {
                return currentData.category===cat
            });

            this.setState({ prod: result(cat) })
            console.log(cat)
        }

        return (
            <Container>
                <Button onClick={() => filterClothes('tech')}> Tech </Button>
                <Wrapper>
                    <ProductImage>
                        <Image_ src={gallery[0]} />
                    </ProductImage>
                    <ProductInfo>
                        <Brand>Apollo</Brand>
                        <Name> {name} </Name>
                        <PriceItems>
                            <CurrencySymbol>$</CurrencySymbol>
                            <Amount> 80.00 </Amount>
                        </PriceItems>
                    </ProductInfo>

                </Wrapper>
            </Container>
        )
    }
}

export default Product

const Container = styled.div`
    // height: 70vh;
    flex: 1;
    // min-width: 450px;
    min-width: 400px;
`
const Wrapper = styled.div`
    // width: 100%;
    display: flex;
    flex-direction: column;
    // justify-content: flex-end;
    // align-items: center;
    padding: 10px;
    width: 400px;
    height: 386px;

`
const ProductImage = styled.div`
    // display: flex;
    // flex-direction: column;
    // align-items: center;
    // justify-content: center;
    // width: 370px;
    // height: 350px;
    margin-bottom: 10px;
    width: 380px;
    height: 370px;

`
