import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { Query } from 'react-apollo'
import { gql } from 'graphql-tag'
import styled from 'styled-components'
import Product from './Product'
import { Attributes } from './Attributes';
import { SideList } from './SideList';

const PRODUCT_QUERY = gql`
    query productQuery($id: String!){ 
        product(id: $id){
            inStock
            name
            id
            brand
            description
            gallery

            attributes{
                name
                type
                id
                items{
                    value
                    displayValue
                    id
                }
            }

            prices{
                amount
                currency{
                    label
                    symbol
                }
            }
      }
    }
`

export class Cart extends Component {
  static propTypes = {

  }

  render() {
    const { id } = this.props
    return (
      // <Container>
      //   <Title>CART</Title>
      //   <Wrapper>
      //     <LeftContainer>

      //     </LeftContainer>

      //     <RightContainer>
      //     world
      //     </RightContainer>
      //   </Wrapper>
      // </Container>

      <Container>
        <Title>CART</Title>
        <LeftContainer>
          <Query query={PRODUCT_QUERY} variables={{ id }}>
            {
              ({ loading, data, error }) => {
                if (loading) return <h1>Loading...</h1>
                if (error) console.log(error)

                const { id, prices, gallery, name, brand, description, inStock, attributes } = data.product           
                // <Cart />
                return <Wrapper>
                  <SideImages>
                    {
                      gallery.map((gallery_item, index) => (
                        <SideList key={gallery_item.index} gallery_item={gallery_item} />
                      ))
                    }
                  </SideImages>
                  <ProductImg>
                    <Image_ src={gallery[0]} alt=""/> 
                  </ProductImg>
                  <ProductInfo>
                    <Brand>{brand}</Brand>
                    <Name>{name}</Name>
                    <AttributesContainer>
                      {
                        data.product.attributes.map((item) => (
                        <Attributes key={item.id} item={item} />
                        )) 
                      }
                      <PriceInfo>
                        <AttributePrice>PRICE :</AttributePrice>
                        <AttributePrice>
                          {prices[0].currency.symbol} {prices[0].amount}
                        </AttributePrice>
                      </PriceInfo>
                    </AttributesContainer>                 

                  </ProductInfo>
                </Wrapper>
              }
            }
          </Query>
      </LeftContainer>
    </Container>
    )
  }
}

export default Cart

