  flex; 1
`
const Container = styled.div`
  display: flex;
  flex-direction: column;

`
const Wrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`
const SideImages = styled.div`
  margin-top: 100px; 
  margin-right: 25px;
`
const ProductImg = styled.div`
  display: flex;
  box-sizing: border-box;
  flex: 1;
  margin-left: 5px;
  margin-top: 100px;
  justify-content: center;
  max-width: 500px;
   `
const Image_ = styled.img`
  display: flex;
  max-height: 50%;
  border: 0.5px ;
  widht: 400px;
  height: 500px;
   `
const ProductInfo = styled.div`
  // flex:1;
  margin-top: 120px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin-right: 30px;
  margin-left: 40px;
`
