import React, { Component } from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'

export class AttributeItem extends Component {
  static propTypes = {

  }

  render() {
      const {value, displayValue, id} = this.props.it
    return (
      <Container>
          <Wrapper>
              <ColorContainer colorstyle={{backgroundColor:value}}>
                {value}
              </ColorContainer>
          </Wrapper>

      </Container>
    )
  }
}

export default AttributeItem

const Container = styled.div`
  margin-top: 10px;
  margin-bottom: 10px;
`
const Wrapper = styled.div`
  display: flex;
`
const ColorContainer = styled.div`
  margin-top: 0px;
  max-width: 70px;
  max-height: 45px; 
  border: 1px solid #A6A6A6;
  display: flex;
  justify-content: center;
  padding: 8px;
  box-sizing: border-box;
  margin: 6px;
  margin-left: 0;

  &:hover{
      background: #1D1F22;
      color: white;
    }
`
const AttributeType = styled.span`
  font-weight: 700;
  margin-bottom: 10px;
`
