import React, { Component, Fragment } from 'react'
import PropTypes from 'prop-types'
import { Query } from 'react-apollo'
import { gql } from 'graphql-tag'
import styled from 'styled-components';
import { Colors } from './Colors';


export class Size extends Component {
  static propTypes = {}

  render() {
    const { name, type, id, items } = this.props.item
    // {items.filter((key) => console.log(key.value))}
    return (
      <Container>
        <Wrapper> 
         <AttributesContainer>
            <AttributeType>{name} :</AttributeType>
              type === 'swatch' ? 
                items.map(it=>(
                  <Colors key={it.id} it={it}/>
                ))
                :
               <SizeContainer>
                {
                 items.value
                } 
                {/* {Object.keys(items).map((key, ind)=>(console.log(key)))} */}
               </SizeContainer>
          </AttributesContainer>
        </Wrapper>
      </Container>
    )
  }
}

export default Size

const Container = styled.div`
  margin-top: 10px;
  margin-bottom: 10px;
`
const Wrapper = styled.div`
  display: flex;
`
const AttributesContainer = styled.div`
  margin-top: 0px;
`
const AttributeType = styled.span`
  font-weight: 700;
  margin-bottom: 10px;
`
const SizeContainer = styled.div`
  max-width: 63px;
  max-height: 45px; 
  border: 1px solid #A6A6A6;
  display: flex;
  justify-content: center;
  padding: 8px;
  box-sizing: border-box;
  margin: 6px;
  margin-left: 0;
`
