import React, { Component } from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'
import { Link } from 'react-router-dom'
import { Query } from 'react-apollo'
import { gql } from 'graphql-tag'
import { Params } from 'react-router-dom'
import basket from '../images/basket.png'


export class ProductItem extends Component {
    state = {
        prod: []
    }

    static propTypes = {
    }

    render() {
        const { name, brand, gallery, id, description, prices, inStock, category } = this.props.prod;

        return (
            <Container>
                <Wrapper>
                    <ProductImage>
                        <Stock>
                          <p>OUT OF STOCK</p>
                        </Stock>
                        <Image_ src={gallery[0]} />
                        <Link to={`/product/${id}`}>
                        <ProductSelector>
                            <SelectIcon src={basket} />
                        </ProductSelector>
                        </Link>
                    </ProductImage>
                    <ProductInfo>
                        <Brand>Apollo</Brand>
                        <Name>{name}</Name>
                        <PriceItems>
                            {/* <CurrencySymbol>$</CurrencySymbol> */}
                            <Amount>{prices[0].currency.symbol} {prices[0].amount}</Amount>
                        </PriceItems>
                    </ProductInfo>
                </Wrapper>
            </Container>
        )
    }
}

export default ProductItem

const ProductSelector = styled.div`
position: absolute;
right: 15%;
bottom: 0;
width: 52px;
height: 52px;
opacity: 0;
`
const Container = styled.div`
    flex: 1;
    min-width: 400px;
    margin-bottom: 15px;
    position: relative;

    &:hover ${ProductSelector}{
        opacity: 1
    }
`
const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
    padding: 10px;
    width: 400px;
    height: 386px;
`
const ProductImage = styled.div`
    margin-bottom: 10px;
    width: 380px;
    height: 370px;
    position: relative;

    &:hover{
        opacity: 0.5;
    }
`
const Stock = styled.div`
    position: absolute;
    color: red;
`
const ProductInfo = styled.div`
    // margin-left: 5px;
`
const PriceItems = styled.div`
    display: flex;
    margin-top: 5px;
`
const CurrencySymbol = styled.div`
    margin-right: 5px;
`
const Amount = styled.div`

`

const Brand = styled.span`
    font-size: 16px;
    font-weight: 100;
    margin-right: 5px;
`
const Name = styled.span`
    font-weight: 100;
`
const SelectIcon = styled.img`
    padding: 15px;
    width: 25px;
    height: 22px;
    background-color: #5ECE7B;
    justify-content: center;
    align-items: center;
    border-radius: 50%;
    transition: all 0.5s ease

    &:hover{
        background-color: #5ECE7B;
        transform: scale(1.1);
    }
  `
