import React, { Component } from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'
import { Link, params } from 'react-router-dom';


export class CategoryItem extends Component {
  static propTypes = {}
  state = {
    All: [],
    Clothes: [],
    Tech: []
  }

  render() {
    // const { catItem: { name, products: { brand } } } = this.props
    const { catItem: {name} }  = this.props 
    // console.log(name);
    // const { catName } = this.props.match.Params.name; // grab params in url
    return (
      <Container>
        <Wrapper>
          <ProductImage>
              <Image_ src = {''} />
            </ProductImage>
            <ProductInfo>
              <Brand>Apollo</Brand>
              <Name>{}</Name>
              <PriceItems>
                <CurrencySymbol>$</CurrencySymbol>
                <Amount>80.00</Amount>
              </PriceItems>
            </ProductInfo>

          <Link to={`/category/${name}`} style={{textDecoration:"none"}}>
            <p>Hello {name}</p> 
          </Link>
        </Wrapper>
      </Container>
    )
  }
}

export default CategoryItem

const Container = styled.div`
    flex: 1;
    min-width: 400px;
`
const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
    padding: 10px;
    width: 400px;
    // height: 386px;

`
const ProductImage = styled.div`
    margin-bottom: 10px;
    width: 380px;
    height: 370px;

`
const ProductInfo = styled.div`

`
const PriceItems = styled.div`
    display: flex;
    margin-top: 5px;
`
const CurrencySymbol = styled.div`
    margin-right: 5px;
`
const Amount = styled.div`

`
const Image_ = styled.img`
    object-fit: none;
    width: 356px;
    height: 338px;

`
const Brand = styled.span`
    font-size: 16px;
    font-weight: 100;
    margin-right: 5px;
`
const Name = styled.span`
    font-weight: 100;
`
const Button = styled.div`
  font-weight: 600;
  color: white;
  background-color: #5ECE7B;
  height: 40px;
  width: 250px;
  display: flex;
  justify-content: center;
  border-radius: 3px; 
  opacity: 0.85;
  font-size: 13px;
  cursor: pointer;
  margin-top: 20px;
  align-items: center;  
`
const CatTitle = styled.p`
  text-decoration: none;
  display: flex;
`
