import React, { Component, Fragment } from 'react'
import PropTypes from 'prop-types'
import { Query } from 'react-apollo'
import { gql } from 'graphql-tag'
import styled from 'styled-components';
import { Navbar } from './Navbar';
import Size from './Size'

const PRODUCT_QUERY = gql`
    query productQuery($id: String!){ 
        product(id: $id){
            inStock
            name
            id
            brand
            description
            gallery
            attributes{
                id
                items{
                    value
                    displayValue
                    id
                }
            }
            prices{
                amount
                currency{
                    label
                    symbol
                }
            }
      }
    }
`

export class Product extends Component {
  static propTypes = {}

  componentDidMount() {
    this.setState({ mounted: true })
  }

  render() {
    let { id } = this.props.match.params 
    return(
      <Fragment>
        <Navbar />
        <Query query={PRODUCT_QUERY} variables={{ id }}>
          {
            ({ loading, data, error }) => {
              if (loading) return <h1>Loading...</h1>
              if (error) console.log(error)

              const { id, prices, gallery, name, brand, description, inStock, attributes } = data.product
              // const { items:{displayValue} } = data.product.attributes 
              return <Wrapper>
                <ProductImg>
                  <Image_ src={gallery[0]} alt="" />
                </ProductImg>
                <ProductInfo>
                  <Brand>{brand} </Brand>
                  <Name>{name}</Name>
                  <AttributesContainer>
                    {
                      data.product.attributes.map(item => (
                        <Size key={item.id} item={item}/> 
                        ))
                      }
                    <Price>
                      <AttributeType>PRICE:</AttributeType>
                      <AttributeType>
                        {prices[0].currency.symbol} {prices[0].amount}
                      </AttributeType>
                    </Price>
                  </AttributesContainer>
                  <Button>ADD TO CART </Button>
                  <ProductDescription>{description}</ProductDescription>
                </ProductInfo>
              </Wrapper>
            }
          }
        </Query>
      </Fragment>
    )
  }
}

export default Product

const Container = styled.div`

`
const Wrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`
const ProductImg = styled.div`
  box-sizing: border-box;
  flex: 2;
  margin-left: 50px;
  margin-top: 100px;
  justify-content: flex-end;
   `
const Image_ = styled.img`
  display: flex;

  max-height: 50%;
  border: 0.5px ;
  widht: 500px;
  height: 500px;
   `
const ProductInfo = styled.div`
  margin-top: 120px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  flex:1;
  margin-right: 50px;
`
const Brand = styled.span`
  font-weight: 400;
  font-size: 30px;
  line-height: 27px;
  margin-top: 15px;
  margin-bottom: 15px;
`
const Name = styled.span`
  line-height: 27px;
  width: 192;
  display: flex;
  align-items: center;
  font-weight: 250;
  font-size: 30px;
  font-style: normal;
  color: #1D1F22;
`
const AttributesContainer = styled.div`
  margin-top:20px;
`
const AttributeType = styled.span`
  font-weight: 700;
  margin-bottom: 10px;

`
const AttributeItems = styled.div`
  margin-top: 10px;
  margin-bottom: 25px;
  display: flex;

`
const Price = styled.span`
  display: flex;
  flex-direction: column;

`
const Button = styled.div`
  font-weight: 600;
  color: white;
  background-color: #5ECE7B;
  height: 40px;
  width: 250px;
  display: flex;
  justify-content: center;
  border-radius: 3px; 
  opacity: 0.85;
  font-size: 13px;
  cursor: pointer;
  margin-top: 20px;
  align-items: center;  
`
const ProductDescription = styled.div`
  width: 256px;
  display: flex;
  flex-wrap: wrap; 
  font-size: 16px;
  margin-top: 5px;
`
const SizeContainer = styled.div`
  max-width: 63px;
  max-height: 45px; 
  border: 1px solid #A6A6A6;
  display: flex;
  justify-content: center;
  padding: 8px;
  box-sizing: border-box;
  margin: 6px;
  margin-left: 0;
`
const ColorContainer = styled.div`

`
const Color = styled.div`

`
