import { gql } from 'graphql-tag'

const PRODUCT_QUERY = gql`
    query productQuery($title: String!){ 
        category(input: {title: $title}){
          name
          products{
            inStock
            name
            id
            brand
            description
            gallery

            attributes{
              id
              type
              name

              items{
                id
                value
                displayValue
              }
            }

            prices{
              amount
              currency{
                label
                symbol
              }
            }
          }
      }
    }
`
export { PRODUCT_QUERY }
