import React, { Component } from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'


export class CategoryItem extends Component {
  static propTypes = {

  }

  render() {
    let {prod} = this.props
    // console.log(products)

    return (
      <Container>
        <Wrapper>
          <ProductImage>
            <Image_ src = {prod[0].gallery} />
          </ProductImage>
          <ProductInfo>
            <Brand>Apollo</Brand>
            <Name>{prod[0].name}</Name>
            <PriceItems>
              <CurrencySymbol>$</CurrencySymbol>
              <Amount>80.00</Amount>
            </PriceItems>
          </ProductInfo>
        </Wrapper>
      </Container>
    )
  }
}

export default CategoryItem

const Container = styled.div`
    // height: 70vh;
    flex: 1;
    // min-width: 450px;
    min-width: 400px;
`
const Wrapper = styled.div`
    // width: 100%;
    display: flex;
    flex-direction: column;
    // justify-content: flex-end;
    // align-items: center;
    padding: 10px;
    width: 400px;
    height: 386px;

`
const ProductImage = styled.div`
    // display: flex;
    // flex-direction: column;
    // align-items: center;
    // justify-content: center;
    // width: 370px;
    // height: 350px;
    margin-bottom: 10px;
    width: 380px;
    height: 370px;

`
const ProductInfo = styled.div`

`
const PriceItems = styled.div`
    display: flex;
    margin-top: 5px;
`
const CurrencySymbol = styled.div`
    margin-right: 5px;
`
const Amount = styled.div`

`
const Image_ = styled.img`
    // margin-bottom: 5px;
    // object-fit: cover;
    // object-position: 30% 0;
    // display: table;
    // display: block;
    object-fit: none;
    width: 356px;
    height: 338px;

`
const Brand = styled.span`
    font-size: 16px;
