import React, { Component } from 'react'
// import PropTypes from 'prop-types'
import styled from 'styled-components'

export class product extends Component {
  static propTypes = {

  }

  render() {
    return (
      <Container>
        <Wrapper>
            <ProductImg>
                <Image_ src = "https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087" 
                alt=""/>
            </ProductImg>
            <ProductInfo>
              {/* <NameBrandContainer> */}
                <Name>Apollo</Name>
                <Brand>Running Short</Brand>
                <AttributesContainer>
                  <AttributeType>SIZE</AttributeType>
                  <AttributeItems>4,6,9,2</AttributeItems>
                  <AttributeType>Color</AttributeType> 
                  <AttributeItems>4,6,9,2</AttributeItems>   
                  <Price>$50.00</Price>
                </AttributesContainer>
              {/* </NameBrandContainer> */} 
              <button></button>
              <ProductDescription>
                \n<div>\n
                  <ul>\n
                    <li>
                      <span>Hardware-beschleunigtes Raytracing macht dein Spiel noch realistischer</span>
                    </li>\n
                    <li>
                      <span>Spiele Games mit bis zu 120 Bilder pro Sekunde</span>
                    </li>\n        
                    <li>
                      <span>Minimiere Ladezeiten mit einer speziell entwickelten 512GB NVMe SSD und wechsle mit Quick Resume nahtlos zwischen mehreren Spielen.</span>
                      </li>\n        <li><span>Xbox Smart Delivery stellt sicher, dass du die beste Version deines Spiels spielst, egal, auf welcher Konsole du spielst</span></li>\n        <li><span>Spiele deine Xbox One-Spiele auf deiner Xbox Series S weiter. Deine Fortschritte, Erfolge und Freundesliste werden automatisch auf das neue System übertragen.</span></li>\n        <li><span>Erwecke deine Spiele und Filme mit innovativem 3D Raumklang zum Leben</span></li>\n        <li><span>Der brandneue Xbox Wireless Controller zeichnet sich durch höchste Präzision, eine neue Share-Taste und verbesserte Ergonomie aus</span></li>\n        <li><span>Ultra-niedrige Latenz verbessert die Reaktionszeit von Controller zum Fernseher</span></li>\n        <li><span>Verwende dein Xbox One-Gaming-Zubehör -einschließlich Controller, Headsets und mehr</span></li>\n        <li><span>Erweitere deinen Speicher mit der Seagate 1 TB-Erweiterungskarte für Xbox Series X (separat erhältlich) und streame 4K-Videos von Disney+, Netflix, Amazon, Microsoft Movies &amp; TV und mehr</span></li>\n    </ul>\n</div>
              </ProductDescription>
            </ProductInfo>
        </Wrapper>
      </Container>
    )
  }
}

export default product

const Container = styled.div`

`
const Wrapper = styled.div`
  display: flex;
  justify-content: center;

`
const ProductImg = styled.div`
  margin-left: 150px
   `
const Image_ = styled.img`
  height: 100vh;
  display: flex;
  justify-content: flex-start;
  border: 0.5px ;

   `
const ProductInfo = styled.div`
  display: flex;
  flex-direction: column;
  position: absolute;
  top: 0;
  right: 0;
  // margin 100px;
`
// const NameBrandContainer = styled.div`


// `
const Brand = styled.span`

`
const Name = styled.span`
  // font-weight: 600;
`
const AttributesContainer = styled.div`

`
const AttributeType = styled.span`

`
const AttributeItems = styled.div`

`
const Price = styled.span`

`
const Button = styled.div`

`
const ProductDescription = styled.span`

