import React, { Component } from 'react'
import PropTypes from 'prop-types'
import { Query } from 'react-apollo'
import { gql } from 'graphql-tag'
import styled from 'styled-components'
import Product from './Product'

const PRODUCT_QUERY = gql`
    query productQuery($id: String!){ 
        product(id: $id){
            inStock
            name
            id
            brand
            description
            gallery

            attributes{
                name
                type
                id
                items{
                    value
                    displayValue
                    id
                }
            }

            prices{
                amount
                currency{
                    label
                    symbol
                }
            }
      }
    }
`

export class Cart extends Component {
  static propTypes = {

  }

  render() {
    const {id} = this.props
    return (
      // <Container>
      //   <Title>CART</Title>
      //   <Wrapper>
      //     <LeftContainer>
      //       world
      //       <Product />
      //     </LeftContainer>

      //     <RightContainer>
      //     world
      //     </RightContainer>
      //   </Wrapper>
      // </Container>

      <Container>
      {/* <Categories /> */} 
      <Query query={PRODUCT_QUERY} variables={{ id }}>
        {
          ({ loading, data, error }) => {
            if (loading) return <h1>Loading...</h1>
            if (error) console.log(error)

            const { id, prices, gallery, name, brand, description, inStock, attributes } = data.product           
            // <Cart />
            return <Wrapper>
              <SideImages>
                {
                  gallery.map((gallery_item, index) => (
                    <SideList key={gallery_item.index} gallery_item={gallery_item} />
                  ))
                }
              </SideImages>
              <ProductImg>
                <Image_ src={gallery[0]} alt=""/> 
              </ProductImg>
              <ProductInfo>
                <Brand>{brand}</Brand>
                <Name>{name}</Name>
                <AttributesContainer>
                  {
                    data.product.attributes.map((item) => (
                    <Attributes key={item.id} item={item} />
                    )) 
                  }
                  <PriceInfo>
                    <AttributePrice>PRICE :</AttributePrice>
                    <AttributePrice>
                      {prices[0].currency.symbol} {prices[0].amount}
                    </AttributePrice>
                  </PriceInfo>
                </AttributesContainer> 
                <Link to={'/cart'} style={{textDecoration: 'none'}}>
                  <Button>ADD TO CART</Button>
                </Link>                   
                <ProductDescription><div dangerouslySetInnerHTML={{__html: description}} /></ProductDescription>
              </ProductInfo>
            </Wrapper>
          }
        }
      </Query>
    </Container>
    )
  }
}

export default Cart

// const Container = styled.div`
//   display: flex;
//   flex-direction: column;
//   margin: 30px;
// `
// const Wrapper = styled.div`
//   display: flex;
// `
const Title = styled.div`
  margin-bottom: 30px;
`
const LeftContainer = styled.div`
  flex: 1
`
const RightContainer = styled.div`
  flex; 1
`
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center; 
`
