//           brand
//           inStock
//           category
//           attributes{
//             name
//             id
//             type
//           }
//           }
//         }
// `

const PRODUCT_QUERY = gql`
    query productQuery($title: Sting){ 
        category(input: {$title: String}){
          name
          products{
            inStock
            name
            id
            brand
            description
            gallery
          }
          }
      }
`
// ($name: String!)
// (name: $name)

export class Products extends Component {


  render() {

    return (

      <Container>
        <Query query={PRODUCT_QUERY} >
          {
            ({ data, loading, error }) => {
              if (loading) return <h4>Loading</h4>
              if (error) console.log(error.message)
              // console.log(data.category.products) 
              // const catego = data.category

              return <Wrap>
                {/* <Product gallery={gallery}/> */}     
                { 
                  data.category.products.map(prod => (
                    <Product key={prod.id} prod={prod}/>
                  ))
                }
              </Wrap>
            }
          }
        </Query>
      </Container>
    )
  }
}

export default Products

const Container = styled.div`
  // display: flex;
  padding: 10px;
`
const Wrap = styled.div`
  display: flex;
  flex-wrap: wrap;
  margin-left: 20px;
`

