import React, { Component, Fragment } from 'react'
import PropTypes from 'prop-types'
import { Query } from 'react-apollo'
import { gql } from 'graphql-tag'
import styled from 'styled-components'
import { Category_bar } from './Category_bar';
import Product from './Product'
import {GET_CATEGORIES} from './Data'

// const CATEGORIES_QUERY = gql`
//     query CategoriesQuery($id: String!){ 
//         product(id: $id){
//             inStock
//             id
//             description
//             name
//             gallery
//             }
//         }
// `

const PRODUCT_QUERY = gql`
    query productQuery($id = String!){ 
        product(id: $id){
          name
          id
          gallery
          description
          brand
          inStock
          category
          attributes{
            id
            name
            type
            items{

            }
          }
        }
    }
`

export class Products extends Component {
  static propTypes = {

  }

  render() {

    return (
      <Container>

        {/* variables={{products}} */}
        <Query query={PRODUCT_QUERY} >
          {
            ({data, loading, error}) => {
              if(loading) return <h3>Loading</h3>
              if(error) console.log(error.message)

              return <Wrap>
                {
                data.categories.map(cat => (
                  <Product key={cat.name} cat={cat}/>
              ))
                }
                {/* {
                 console.log(data.categories[1].products[0].gallery)
                } */}
              </Wrap>

            }
          }
        </Query>
      </Container>

    )
  }
}

export default Products

const Container = styled.div`
  // display: flex;
  padding: 10px;
  // flex-wrap: wrap;
`
const Wrap = styled.div`
  display: flex;

`
