import React, { Component } from 'react'
// import PropTypes from 'prop-types'
import styled from 'styled-components'

export class product extends Component {
  static propTypes = {

  }

  render() {
    return (
      <Container>
        <Wrapper>
            <ProductImg>
                <Image_ src = "https://cdn.shopify.com/s/files/1/0087/6193/3920/products/DD1381200_DEOA_2_720x.jpg?v=1612816087" 
                alt=""/>
            </ProductImg>
            <ProductInfo>
              {/* <NameBrandContainer> */}
                <Name>Apollo</Name>
                <Brand>Running Short</Brand>
                <AttributesContainer>
                  <AttributeType>SIZE</AttributeType>
                  <AttributeItems>4,6,9,2</AttributeItems>
                  <AttributeType>Color</AttributeType> 
                  <AttributeItems>4,6,9,2</AttributeItems>   
                  <Price>$50.00</Price>
                </AttributesContainer>
              {/* </NameBrandContainer> */} 
              <Button>ADD TO CART</Button>
              <ProductDescription>
                <br/><p>A good gaming console. Plays games of PS4! Enjoy if you can buy it mwahahahaha</p>
              </ProductDescription>
            </ProductInfo>
        </Wrapper>
      </Container>
    )
  }
}

export default product

const Container = styled.div`

`
const Wrapper = styled.div`
  display: flex;
  justify-content: center;

`
const ProductImg = styled.div`
  margin-left: 150px
   `
const Image_ = styled.img`
  height: 100vh;
  display: flex;
  justify-content: flex-start;
  border: 0.5px ;

   `
const ProductInfo = styled.div`
  display: flex;
  flex-direction: column;
  flex-position: absolute;
  left: 929
  // align-items: center
  // position: absolute;
  // top: 0;
  // right: 0;
  // margin 100px;
`
// const NameBrandContainer = styled.div`


// `
const Brand = styled.span`
  font-weight: 400;
  font-size: 30px;
  line-height: 27px;
`
const Name = styled.span`
  line-height: 27px;
  width: 192;
  display: flex;
  align-items: center;
  font-weight: 500;
  font-size: 30px;
  font-style: normal;
  color: #1D1F22;
`
const AttributesContainer = styled.div`

`
const AttributeType = styled.span`

`
const AttributeItems = styled.div`

`
const Price = styled.span`

`
const Button = styled.div`
  font-weight: 600;
  color: white;
  background-color: #5ECE7B;
  height: 40px;
  width: 256px;
  display: flex;
  justify-content: center;
  border-radius: 3px; 
  opacity: 0.85;
  font-size: 13px;
  cursor: pointer;
  margin-top: 20px;
  align-items: center;  

`
const ProductDescription = styled.div`
  width: 256px;
  display: flex;
  flex-wrap: wrap;

`
