import React, { Component } from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'
import { CategoryItem } from './CategoryItem';
import { Query } from 'react-apollo'
import { gql } from 'graphql-tag'
import CategoryName from './CategoryName'
import Navbar from './Navbar'


class Categories extends Component {
  constructor(props) {
    super(props);
    this.getCategories = this.getCategories
    this.state = {
      categories: []
    }
  }

  getCategories(data){
    this.setState({categories: data})
  }
  static propTypes = {}

  // filterClothes = (cat) => {
  //   const results = this.data.categories.filter((catItem) => {
  //     return catItem.name === catItem.name
  //     console.log(catItem[0].name)
  //   })
  //   this.setState({clothes_data: results})
  // }

  render() {
    const CATEGORIES_QUERY = gql`
    query categoriesQuery{ 
        categories{
          name
          products{
            name
            brand
            id
            gallery
            description
            inStock
            category
          }
        }
    }
`
    return (
      <Container>
        <Query query={CATEGORIES_QUERY} onCompleted={(e)=>this.getCategories(e)}>
          {
            ({ data, loading, error }) => {
              if (loading) return <h3>loading in categories</h3>
              if (error) console.log(error.message)

              return <Wrap>
                {
                  data.categories.map(catItem => (
                    <CategoryItem key={catItem.name} catItem={catItem} />
                  ))
                }
              </Wrap>
            }
          }
        </Query>
      </Container>
    )
  }
}

export default Categories

const Container = styled.div`
  // display: flex;
`
const Wrap = styled.div`
   display: flex;
   flex-direction: column;
`
const Button = styled.div`
  font-weight: 600;
  color: white;
  background-color: #5ECE7B;
  height: 40px;
  width: 250px;
  display: flex;
  justify-content: center;
  border-radius: 3px; 
  opacity: 0.85;
  font-size: 13px;
  cursor: pointer;
  margin-top: 20px;
  align-items: center;  
  `
